using System;
using System.Threading;
using rps.models;

bool playing = true;
Game RPS = new Game();

int compScore = 0;
int playerScore = 0;

string intro = @"                                
    //   ) )  //   ) ) //   ) ) 
   //___/ /  //___/ / ((        
  / ___ (   / ____ /    \\      
 //   | |  //             ) )   
//    | | //       ((___ / /    ";
string intro2 = "N to stop playing, or any R P or S to play.";

while(playing)
{
  Console.Clear();
  Console.WriteLine(intro);
  for (int i = 0; i < intro2.Length; i++)
  {
    Console.Write(intro2[i]);
    Thread.Sleep(20);
  }
  Console.WriteLine("");
  Console.WriteLine($"Player Score: {playerScore} - - - - - Enemy Kill Count {compScore}");
  string keepPlaying = Console.ReadLine();


  if (keepPlaying == "n") {
    Console.WriteLine("Scrub");
    playing = false;
  }
  if (keepPlaying == "r" || keepPlaying ==  "p" || keepPlaying ==  "s") {

    string result = RPS.PlayRound(keepPlaying);
    Console.WriteLine(result);

    if (result == "Win") {
      playerScore += 1;
      superMario();
    }
    
    if (result =="Loser") {
      compScore += 1;
    }
    Thread.Sleep(2000);
  }
}

